package com.iusmob.adklein.demo.activity;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import androidx.appcompat.app.AppCompatActivity;import com.iusmob.adklein.demo.R;public class NativeActivity extends AppCompatActivity implements View.OnClickListener {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_native);        findViewById(R.id.btnNativeAd).setOnClickListener(this);        findViewById(R.id.btnNativeAdRecycleView).setOnClickListener(this);        findViewById(R.id.btnNativeExpressAd).setOnClickListener(this);        findViewById(R.id.btnNativeExpressAdRecycleView).setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.btnNativeAd:                startActivity(NativeAdActivity.class);                break;            case R.id.btnNativeAdRecycleView:                startActivity(NativeAdListActivity.class);                break;            case R.id.btnNativeExpressAd:                startActivity(NativeExpressAdActivity.class);                break;            case R.id.btnNativeExpressAdRecycleView:                startActivity(NativeExpressAdListActivity.class);                break;            default:                break;        }    }    private void startActivity(Class<? extends Activity> activityClass) {        Intent intent = new Intent(this, activityClass);        startActivity(intent);    }}